import { configureStore } from "@reduxjs/toolkit";

let state = {
    user : {
        firstname:"",
        lastname:""
    },
    token: "",
    logged : false,
    remember : false,
    editMode : false
}

const reducer = (currentState, action) => {
    switch (action.type) {
        case "setFirstname": {
            const user = {...currentState.user,firstname: action.payload}
            return {...currentState, user}
        }
        case "setLastname": {
            const user = {...currentState.user,lastname: action.payload}
            return {...currentState, user}
        }
        case "setToken": {
            return {...currentState, token: action.payload}
        }
        case "setLogged": {
            return {...currentState,logged: action.payload}
        }
        case "setRemeber": {
            return {...currentState,remember: action.payload}
        }
        case "editMode": {
            return {...currentState,editMode: action.payload}
        }
    
        default:
            return currentState;
    }
}

export const store = configureStore({
    preloadedState: state,
    reducer
})
