import "../../style/pages/login.css"
import { useState } from "react"
import { useDispatch } from "react-redux"

function Login () {

    const dispatch = useDispatch()

    const [formData, setFormData] = useState({
        username:"",
        password:"",
        remember:false
    })
    /*
    const handleChange = (e) => {
        setFormData({...formData, [e.target.name]:e.target.value})
        console.log(formData)
    }

    const toggleRemember = (e) => {
        console.log(formData)
        let newValue = !formData.remember
        setFormData({...formData, remember: newValue})
        console.log(formData)
    }
    */
    const handleSubmit = (e) => {
        
        e.preventDefault()
        const data = new FormData(e.target)
        data.set("username", data.get("username"))
        data.set("password", data.get("password"))
        data.set("remember",data.get("remember"))
        fetch("http://localhost:3001/api/v1/user/login", {
            method:"POST",
            body: {
                "email":formData.username,
                "password":formData.password
            }
        }).then(response => {
            if (response.status === 200) {
                dispatch({type: "setToken",payload: response.body.token})
                console.log(response.body.token)
            } else {
                return "Error" + response.status
            }
        })
    }

    return (
        <main className="main bg-dark">
            <section className="sign-in-content">
                <i className="fa fa-user-circle sign-in-icon"></i>
                <h1>Sign In</h1>
                <form onSubmit={handleSubmit}>
                    <div className="input-wrapper">
                        <label htmlFor="username">Username</label>
                        <input type="text" name="username" value={formData.username} onChange={handleChange} />
                    </div>
                    <div className="input-wrapper">
                        <label htmlFor="password">Password</label>
                        <input type="password" name="password" value={formData.password} onChange={handleChange}/>
                    </div>
                    <div className="input-remember">
                        <input type="checkbox" name="remember-me" onChange={toggleRemember}/>
                        <label htmlFor="remember">Remember me</label>
                    </div>
                    <button className="sign-in-button" type="submit">Sign In</button>
                </form>
            </section>
        </main>
    )
}

export default Login
