import "../../style/components/header.css"
import logo from "../../assets/img/argentBankLogo.png"
import { Link } from "react-router-dom"
import { useEffect,useState } from "react"
import { useNavigate } from "react-router-dom"
import { useStore } from "react-redux"
import useToken from "../../utils/useToken"

function Header () {

    const store = useStore()
    const navigate = useNavigate()
    const [token,setToken] = useState(store.getState().token)
    let userToken = useToken(token)
    const [user, setUser] = useState(null)

    useEffect(() => {
        store.subscribe(()=> {
            setToken(store.getState().token)
        })
    },[store])

    useEffect(() => {
        const fetchUser = async () => {
            const userData = userToken
            setUser(userData)
        }
        fetchUser()
    },[userToken])

    const logOff = () => {
        localStorage.clear()
        store.dispatch({type:"setToken",payload:""})
        setUser("invalid")
        navigate("/")
    }

    if (!user || user === "invalid") {

        return (
            <header className="main-nav">
                <Link className="main-nav-logo" to="/">
                    <img className="main-nav-logo-image" src={logo} alt="Argent Bank Logo"/>
                    <h1 className="sr-only">Argent Bank</h1>
                </Link>
                <div>
                    <Link className="main-nav-item" to="/sign-in">
                        <i className="fa fa-user-circle"></i>
                        Sign In
                    </Link>
                </div>
            </header>
        )   
    } 

    return (
        <header className="main-nav">
            <Link className="main-nav-logo" to="/">
                <img className="main-nav-logo-image" src={logo} alt="Argent Bank Logo"/>
                <h1 className="sr-only">Argent Bank</h1>
            </Link>
            <div>
                <Link className="main-nav-item" href="./user.html">
                    <i className="fa fa-user-circle"></i>
                    {user.firstName}
                </Link>
                <Link className="main-nav-item" to="/" onClick={logOff}>
                    <i className="fa fa-sign-out"></i>
                    Sign Out
                </Link>
            </div>
        </header>
    )
    
}

export default Header
