import { configureStore } from "@reduxjs/toolkit";

let state = {
    token: "",
    logged : false,
    editMode : false
}

const reducer = (currentState, action) => {
    switch (action.type) {
        
        case "setToken": {
            return {...currentState, token: action.payload}
        }
        case "setLogged": {
            return {...currentState,logged: action.payload}
        }
        case "editMode": {
            return {...currentState,editMode: action.payload}
        }
    
        default:
            return currentState;
    }
}

export const store = configureStore({
    preloadedState: state,
    reducer
})
