import "../../style/components/nameEditor.css"
import { useState,useEffect,useRef } from "react"
import { useStore } from "react-redux"

function NameEditor () {

    const store = useStore()
    const firstnameRef = useRef()
    const lastnameRef = useRef()
    const [editMode,setEditMode] = useState(store.getState().editMode)

    useEffect (()=> {
        store.subscribe(()=> {
            setEditMode(store.getState().editMode)
        })
    },[store])

    const handleSubmit = async (e) => {

        e.preventDefault()
        let firstNameData = firstnameRef.current.value
        let lastnameData = lastnameRef.current.value
        let token = store.getState().token

        try {
            const submitResponse = await fetch("http://localhost:3001/api/v1/user/profile", {
                method:"PUT",
                headers:{
                    "content-type":"application/json; charset=utf-8",
                    "Authorization":"Bearer " + token
                    },
                mode:"cors",
                body:JSON.stringify({
                    "firstname": firstNameData,
                    "lastname": lastnameData
                })
            })
            if (submitResponse.status === 200) {
                const submitData = await submitResponse.json()
                console.log(submitData)
            }
            store.dispatch({type:"editModeOff"})
        } catch (error) {
            console.error("An error occured: ", error)
        }
    } 

    if (editMode === true) {
        return (
            <form className="nameEditor" onSubmit={handleSubmit}>
                <div>
                    <input name="firstname" ref={firstnameRef} placeholder={store.getState().user.firstname}></input>
                    <input name="lastname" ref={lastnameRef} placeholder={store.getState().user.lastname}></input>
                </div>
                <div>
                    <button type="submit">Save</button>
                    <button onClick={()=> store.dispatch({type:"editModeOff"})}>Cancel</button>
                </div>
            </form>
        )
    } else {
        return (
            <button className="edit-button" onClick={()=> store.dispatch({type:"editModeOn"})}>Edit Name</button>
        )
    }
}

export default NameEditor
