import "../../style/components/nameEditor.css"
import { useState,useEffect } from "react"
import { useStore } from "react-redux"

function NameEditor () {

    const store = useStore()
    const [editMode,setEditMode] = useState(store.getState().editMode)

    useEffect (()=> {
        store.subscribe(()=> {
            setEditMode(store.getState().editMode)
        })
    },[store])

    if (editMode === true) {
        return (
            <form className="nameEditor">
                <input name="firstname"></input>
                <input name="lastname"></input>
                <button onClick={store.dispatch("editModeOff")}>Save</button>
                <button onClick={store.dispatch("editModeOff")}>Cancel</button>
            </form>
        )
    } else {
        <button className="edit-button" onClick={store.dispatch("editModeOn")}>Edit Name</button>
    }
}

export default NameEditor
