import "../../style/components/nameEditor.css"
import { useState,useEffect,useRef } from "react"
import { useStore } from "react-redux"

function NameEditor ({firstName,lastName}) {

    const store = useStore()
    const [editMode,setEditMode] = useState(false)
    const [firstNameData, setFirstNameData] = useState(firstName || "")
    const [lastNameData, setLastNameData] = useState(lastName || "")

    const toggleMode = () => {
        setEditMode(current => !current)
    }

    const handleSubmit = async (e) => {

        e.preventDefault()
        let token = store.getState().token

        try {
            console.log("Submit lancé")
            console.log(firstNameData)
            console.log(lastNameData)
            console.log(token)
            const submitResponse = await fetch("http://localhost:3001/api/v1/user/profile", {
                method:"PUT",
                headers:{
                    "content-type":"application/json; charset=utf-8",
                    "Authorization":"Bearer " + token
                    },
                mode:"cors",
                body:JSON.stringify({
                    "firstname":firstNameData,
                    "lastname":lastNameData
                })
            })
            if (submitResponse.status === 200) {
                const loginData = await submitResponse.json()
                console.log(loginData)
            }
        } catch (error) {
            console.error("An error occured: ", error)
        }
    } 

    if (editMode === true) {
        return (
            <form className="nameEditor" onSubmit={handleSubmit}>
                <div>
                    <input type="text" name="firstname" value={firstNameData} onChange={e => setFirstNameData(e.target.value)} placeholder={firstName}></input>
                    <input type="text" name="lastname" value={lastNameData} onChange={e => setLastNameData(e.target.value)} placeholder={lastName}></input>
                </div>
                <div>
                    <button type="submit">Save</button>
                    <button type="button" onClick={toggleMode}>Cancel</button>
                </div>
            </form>
        )
    } 
    
    return (
        <button type="button" className="edit-button" onClick={toggleMode}>Edit Name</button>
    )
}

export default NameEditor
