import "../../style/components/nameEditor.css"
import { useState,useEffect,useRef } from "react"
import { useStore } from "react-redux"

function NameEditor ({firstName,lastName}) {

    const store = useStore()
    const firstnameRef = useRef()
    const lastnameRef = useRef()
    const [editMode,setEditMode] = useState(false)
    const [formData,setFormData] = useState(null)

    const toggleMode = () => {
        setEditMode(current => !current)
    }

    const handleSubmit = async (e) => {

        e.preventDefault()
        let firstNameData = firstnameRef.current.value
        let lastnameData = lastnameRef.current.value
        let token = store.getState().token
        setFormData({"firstname":firstNameData,"lastname":lastnameData})

        try {
            console.log("Submit lancé")
            const submitResponse = await fetch("http://localhost:3001/api/v1/user/profile", {
                method:"PUT",
                headers:{
                    "content-type":"application/json; charset=utf-8",
                    "Authorization":"Bearer " + token
                    },
                mode:"cors",
                body:JSON.stringify({
                    formData
                })
            })
            if (submitResponse.status === 200) {
                const loginData = await submitResponse.json()
                console.log(loginData)
            }
            store.dispatch({type:"editModeOff"})
        } catch (error) {
            console.error("An error occured: ", error)
        }
    } 

    if (editMode === true) {
        return (
            <form className="nameEditor" onSubmit={handleSubmit}>
                <div>
                    <input type="text" name="firstname" ref={firstnameRef} placeholder={firstName}></input>
                    <input type="text" name="lastname" ref={lastnameRef} placeholder={lastName}></input>
                </div>
                <div>
                    <button type="submit">Save</button>
                    <button type="button" onClick={toggleMode}>Cancel</button>
                </div>
            </form>
        )
    } 
    
    return (
        <button type="button" className="edit-button" onClick={toggleMode}>Edit Name</button>
    )
}

export default NameEditor
